$(document).ready(function () {
    var winning = 0;
    var losing = 0;
    var total = 0;
    //random number generated by the computer between 19 and 120
    var randomNumber = Math.floor(Math.random() * 120) + 19;
    //random numeber generated by the crystalls between 1 and 12
    var number1 = Math.floor(Math.random() * 12) + 1;
    var number2 = Math.floor(Math.random() * 12) + 1
    var number3 = Math.floor(Math.random() * 12) + 1
    var number4 = Math.floor(Math.random() * 12) + 1
    //displaying the random number onthe html
    $("#_number").text(randomNumber);
    //displaying wins and losses
    $("#_wins").html("wins =" +winning);
    $("#_lose").html("losses =" +losing);
      //reset function
      function reset() {
        randomNumber = Math.floor(Math.random() * 102) + 19;

        $("#_number").text(randomNumber);
        number1 = Math.floor(Math.random() * 12) + 1;
        number2 = Math.floor(Math.random() * 12) + 1;
        number3 = Math.floor(Math.random() * 12) + 1;
        number4 = Math.floor(Math.random() * 12) + 1;
        total = 0;
        $('#_total').text("Total=" +total);
    }

    function smileyFace() {
        winning++;
        alert("you won");
        $("#_wins").html("wins =" +winning);
        reset();
    }

    function sadFace() {
        losing++;
        alert("you lost. Try again");
        $("#_lose").html("losses =" +losing);
        reset();
    }
    // event listeners for crystalls
    $('#crystal1').on("click",function () {
        total +=number1;

        if (randomNumber === total) {
            smileyFace();
        } else if (total > randomNumber) {
            sadFace();
        }
        $('#_total').text("Total=" +total);
        console.log(total);
    });
    $('#crystal2').click(function () {
        total += number2;
        if (randomNumber === total) {
            smileyFace();
        } else if (total > randomNumber) {
            sadFace();
        }
        $('#_total').text("Total=" +total);
        console.log(total);
    })
    $('#crystal3').click(function () {
        total += number3;
        if (randomNumber === total) {
            smileyFace();
        } else if (total > randomNumber) {
            sadFace();

        }
        $('#_total').text("Total=" +total);
        console.log(total);
    })

    $('#crystal4').click(function () {
        total = total + number4;
        if (randomNumber === total) {
            smileyFace();
        } else if (total > randomNumber) {
            sadFace();
        }

        $('#_total').text("Total=" +total);
        console.log(total);
    })
  



})